mapper.update:
UPDATE article
article_user_id = #{articleUserId,jdbcType=INTEGER}, article_title = #{articleTitle,jdbcType=VARCHAR}, article_parent_category_id = #{articleParentCategoryId,jdbcType=INTEGER}, article_child_category_id = #{articleChildCategoryId,jdbcType=INTEGER}, article_tag_ids = #{articleTagIds,jdbcType=VARCHAR}, article_view_count = #{articleViewCount,jdbcType=INTEGER}, article_comment_count = #{articleCommentCount,jdbcType=INTEGER}, article_like_count = #{articleLikeCount,jdbcType=INTEGER}, article_post_time = #{articlePostTime,jdbcType=TIMESTAMP}, article_update_time = #{articleUpdateTime,jdbcType=TIMESTAMP}, article_is_comment = #{articleIsComment,jdbcType=INTEGER}, article_status = #{articleStatus,jdbcType=INTEGER}, article_order = #{articleOrder,jdbcType=INTEGER}, article_content = #{articleContent,jdbcType=LONGVARCHAR},
WHERE article_id=#{articleId}
